<script>
import anime from "animejs";

export default {
  name: "MainTitle",
  data() {
    return {
      writing_time: 4,   // Number of seconds to draw the main title
      done: false,
    }
  },

  mounted() {
    this.title_animation();

  },
  methods: {
    async title_animation() {
      var writing_time = this.writing_time * 1000
      const easy = anime({
        targets: '#easy-mask-path',
        strokeDashoffset: [anime.setDashoffset, 0],
        easing: 'easeInOutSine',
        duration: writing_time / 2,
        direction: 'alternate',
        loop: false
      }).finished
      const admin = anime({
        targets: '#admin-mask-path',
        strokeDashoffset: [anime.setDashoffset, 0],
        easing: 'easeInOutSine',
        delay: writing_time / 2,
        duration: writing_time / 2,
        direction: 'alternate',
        loop: false
      }).finished

      await Promise.all([easy, admin])
      this.done = true;
    }
  },
}
</script>

